<?phpclass WebUser extends CWebUser{	private $_model;		public function getRight()	{		$user = $this->loadUser(Yii::app()->user->id);		return $user->right;	}		public function getIsAdmin()	{		$user = $this->loadUser(Yii::app()->user->id);		if($user)		{			return ($user->right==='0');		}		else		{			return 0;		}	}			protected function loadUser($id=null)     {         if($this->_model===null)         {             if($id!==null)                 $this->_model=User::model()->findByPk($id);         }         return $this->_model;     }}?>